const fetchAndConvert = async (
  url: string,
): Promise<string | ArrayBuffer | null> => {
  const data = await fetch(url)
  const blob = await data.blob()
  return new Promise((resolve) => {
    const reader = new FileReader()
    reader.readAsDataURL(blob)
    reader.onloadend = () => {
      const base64data = reader.result
      resolve(base64data)
    }
  })
}

const createOpenPDF = async (
  headerPdf: any,
  pageMargins: any,
  content: any[],
  repeaterHeader: boolean,
  idSignature?: string,
) => {
  let signatureFooter = {}
  if (idSignature) {
    const signatureBase64 = await fetchAndConvert(
      generateURLAssetsWithToken(idSignature!, {
        quality: '15',
      }),
    )
    signatureFooter = {
      stack: [
        {
          image: signatureBase64,
          width: 100,
          height: 100,
          margin: [-72, -10, 0, 0],
        },
        {
          text: 'Firma',
          bold: true,
          margin: [-72, 0, 0, 0],
          fontSize: 10,
        },
      ],
    }
  }
  const footer: any = (currentPage: number, pageCount: number) => {
    const t = {
      layout: 'noBorders',
      fontSize: 8,
      margin: [72, 0, 0, 72],
      table: {
        widths: ['80%', '20%'],
        body: [
          [
            {
              text: 'P√°gina  ' + currentPage.toString() + ' de ' + pageCount,
              margin: [0, 100, 0, 0],
            },
            currentPage === pageCount ? signatureFooter : {},
          ],
        ],
      },
    }

    return t
  }

  if (!repeaterHeader) {
    content = [headerPdf, ...content]
  }
  const props: TDocumentDefinitions = {
    header: repeaterHeader ? headerPdf : (undefined as any),
    pageSize: 'LETTER',
    pageMargins,
    content,
    footer: footer,
    styles: {
      sectionHeader: {
        bold: true,
        fontSize: 15,
        margin: [0, 15],
      },
      header: {
        bold: true,
        fontSize: 15,
      },
      subheader: {
        fontSize: 16,
        bold: true,
        margin: [0, 10, 0, 5],
      },

      tableHeader: {
        bold: true,
        fontSize: 13,
        color: 'black',
      },
    },
  }
  console.log('props', props)
  const pdfDocGenerator = pdfMake.createPdf(props)
  pdfDocGenerator.open({})
}
